# stack representation using list

# defining a stack 
stack = []

# inserting the elements to stack
# stack follows LIFO method

stack.append('a')
stack.append('b')
stack.append('c')
print("stack after inserting the elements:")
print(stack)

# removing elements from top of the stack
print("popped elements are:")
print(stack.pop())

print(stack.pop())

print(stack.pop())

#print(stack.pop())
print("stack after elements are popped:")
print(stack)

